import React, { useState } from 'react'


import Output from './Output';
import Decoded from './Decoded';
import RotorTypes from './settings/RotorTypes';
import GroundSettings from './settings/GroundSettings';
import RingSettings from './settings/RingSettings';
import ReflectorSettings from './settings/ReflectorSettings';
import PlugboardSettings from './settings/PlugboardSettings';

const Input = () => {

    const [inputMessage, setMessage] = useState();

    function handleChange(event) {
        setMessage(event.target.value);
        console.log(event.target.value);
    }



    return (
        <div className="input-section">     
            <label>Code Input</label>   
            <input 
                className="input-text" 
                id="enigma-input" 
                type="text" 
                placeholder="Input"
                //value={inputMessage}
                //onChange={(event) => setMessage(event.target.value)}
                onChange={handleChange}
            />

            <Output />
            <Decoded />
            <RotorTypes />
            <GroundSettings />
            <RingSettings />
            <ReflectorSettings />
            <PlugboardSettings />


            <p> {inputMessage}</p>
        </div>
    )
}

export default Input
