import React, { useState, useEffect } from 'react'

function Encode() {


    const [inputMessage, setMessage] = useState("");
    const [rotorOne, setRotorOne] = useState('I');
    const [rotorTwo, setRotorTwo] = useState('I');
    const [rotorThree, setRotorThree] = useState('I');

    useEffect(() => {
        isValidInput(inputMessage);
        console.log("Use effect inputmessge: ", inputMessage);
    }, [inputMessage])  //eslint-disable-line react-hooks/exhaustive-deps


    useEffect(() => {
        //console.log("test 1UseEffect rotor1: ", rotorOne)
        //console.log("test 2UseEffect rotor2: ", rotorTwo)
        //console.log("test 3UseEffect rotor3: ", rotorThree)
        //userRotorInput({rotorOne, rotorTwo, rotorThree})
    }, [rotorOne, rotorTwo, rotorThree]) //eslint-disable-line react-hooks/exhaustive-deps



    function isValidInput(inputMessage) {
        if (/^[a-zA-Z]*$/g.test(inputMessage)){
            console.log("its a valid input");
        }
        else{
            console.log("error bad input")
            alert("Please use only the letters A-Z either upper or lower case.")
        }
    }


    return (
        <div>
            <div className="input-section">
                <label>Code Input</label>   
                <input 
                    className="input-text" 
                    id="enigma-input" 
                    type="text" 
                    placeholder="Input"
                    onChange={(event) => setMessage(event.target.value)}
                />
            </div>
            <div>

            </div>

        </div>
    )
}

export default Encode
