import React, { useState, useEffect } from 'react'

function GroundSettings(userInitPos) {

    const [initPosOne, setInitPosingOne] = useState('A')
    const [initPosTwo, setInitPosingTwo] = useState('A')
    const [initPosThree, setInitPosingThree] = useState('A')

    useEffect(() => {
        console.log("initPosOne: ", initPosOne)
        userInitPos({initPosOne, initPosTwo, initPosThree})
    }, [initPosOne, initPosTwo, initPosThree])

    return (
        <div>
            <div className="settings-subsection">
                <label><a href="https://en.wikipedia.org/wiki/Enigma_rotor_details#Rotor_offset">Ground settings / Initial Position (Grundstellung)</a></label>
                <input className="rotor-settings" id="rotor-left-pos" type="text" value="A"/>
                <input className="rotor-settings" id="rotor-middle-pos" type="text" value="A"/>
                <input className="rotor-settings" id="rotor-right-pos" type="text" value="A"/>
            </div>

        </div>
    )
}

export default GroundSettings
