import React, { useState, useEffect } from 'react'




const Input = ( {userTextInput} ) => {

    // store the users input in the inputMessage variable and update that value as the user add more text
    const [inputMessage, setMessage] = useState("");

    // gets the user Input value, validates it, puts it in inputMessage and sends inputMessage up one level to APP.js
    useEffect(() => {
        isValidInput(inputMessage);
        console.log("Use effect inputmessge: ", inputMessage);
        userTextInput({inputMessage})
    }, [inputMessage])

    // user input validation checked in the useEffect call
    function isValidInput(inputMessage) {
        if (/^[a-zA-Z]*$/g.test(inputMessage)){
            console.log("its a valid input");
        }
        else{
            console.log("error bad input")
            alert("Please use only the letters A-Z either upper or lower case.")
        }
    }

    return (
        <div className="input-section">     
            <label>Code Input</label>   
            <input 
                className="input-text" 
                id="enigma-input" 
                type="text" 
                placeholder="Input"
                onChange={(event) => setMessage(event.target.value)}
            />
        </div>
    )
}

export default Input
