import React, { useState, useEffect } from 'react'


const RotorTypes = ({userRotorInput}) => {

    const [rotorOne, setRotorOne] = useState('I');
    const [rotorTwo, setRotorTwo] = useState('I');
    const [rotorThree, setRotorThree] = useState('I');

    useEffect(() => {
        //console.log("test 1UseEffect rotor1: ", rotorOne)
        //console.log("test 2UseEffect rotor2: ", rotorTwo)
        //console.log("test 3UseEffect rotor3: ", rotorThree)
        userRotorInput({rotorOne, rotorTwo, rotorThree})
    }, [rotorOne, rotorTwo, rotorThree]) //eslint-disable-line react-hooks/exhaustive-deps



    return (
        <div>
            <h2>Rotors settings</h2>
            <div className="settings-subsection">
                <label><a href="https://en.wikipedia.org/wiki/Enigma_machine#Rotors">Rotor types (Walzenlage)</a></label>
                <select 
                    className="rotor-type-settings" 
                    id="rotor-type-left"
                    defaultValue={rotorOne}
                    onChange={(event) => setRotorOne(event.target.value)}
                >
                        <option value="I" defaultValue={rotorOne}>I</option>
                        <option value="II">II</option>
                        <option value="III">III</option>
                        <option value="IV">IV</option>
                        <option value="V">V</option>
                </select>

                <select 
                    className="rotor-type-settings" 
                    id="rotor-type-middle"
                    value={rotorTwo}
                    onChange={(event) => setRotorTwo(event.target.value)}
                >
                        <option value="I">I</option>
                        <option value="II" selected>II</option>
                        <option value="III">III</option>
                        <option value="IV">IV</option>
                        <option value="V">V</option>
                </select>

                <select 
                    className="rotor-type-settings" 
                    id="rotor-type-right"
                    value={rotorThree}
                    onChange={(event) => setRotorThree(event.target.value)}
                >
                        <option value="I">I</option>
                        <option value="II">II</option>
                        <option value="III" selected>III</option>
                        <option value="IV">IV</option>
                        <option value="V">V</option>
                </select>
            </div>

        </div>
    )
}

export default RotorTypes
