import React, { useState, useEffect } from 'react'



const ReflectorSettings = ({userReflector}) => {

    const [reflector, setReflector] = useState('UKW-C');

    useEffect(() => {

        userReflector({reflector})

    }, [reflector]) //eslint-disable-line react-hooks/exhaustive-deps

    return (
        <div>
            <h2>Reflector settings</h2>
            <div className="settings-subsection">
                {/* <label><a href="https://en.wikipedia.org/wiki/Enigma_machine#Reflector">Reflector type (Umkehrwalze)</a></label> */}
                <select 
                    defaultValue={"UKW-C"} 
                    className="reflector-settings" 
                    id="reflector-type"
                    onChange={(event) => setReflector(event.target.value)}
                >
                    <option value="UKW-B">UkW-B</option>
                    <option value="UKW-C">UKW-C</option>
                </select>
            </div>
        </div>
    )
}




export default ReflectorSettings
